import { CHECKLIST } from '../content';
import { ChecklistActionTypes } from './checklist.actions';
export const INITIAL_STATE = Object.assign(Object.assign({}, CHECKLIST), {
  filter: {
    categories: 'ALL',
    favorites: 'ALL'
  },
  editMode: false
});
export function checklistReducer(state = INITIAL_STATE, action) {
  switch (action.type) {
    case ChecklistActionTypes.SET_CATEGORIES_FILTER:
    case ChecklistActionTypes.SET_FAVORITES_FILTER:
      return Object.assign(Object.assign({}, state), {
        filter: filterReducer(state.filter, action)
      });
    case ChecklistActionTypes.TOGGLE_EDIT_MODE:
      return Object.assign(Object.assign({}, state), {
        editMode: !state.editMode
      });
    default:
      return state;
  }
}
export const filterReducer = (state, action) => {
  switch (action.type) {
    case ChecklistActionTypes.SET_CATEGORIES_FILTER:
      return Object.assign(Object.assign({}, state), {
        categories: action.payload
      });
    case ChecklistActionTypes.SET_FAVORITES_FILTER:
      return Object.assign(Object.assign({}, state), {
        favorites: action.payload
      });
    default:
      return state;
  }
};
