export const createChecklistItem = (id, itemEntities, projectItems, favorites) => {
  return Object.assign(Object.assign({}, itemEntities[id]), {
    checked: projectItems[id],
    favorite: favorites[id]
  });
};
export const computeScore = (categoryItems, items) => {
  const score = categoryItems.reduce((acc, id) => {
    return items[id] ? acc + 1 : acc;
  }, 0);
  return calculatePercentage(score, categoryItems.length);
};
export const calculatePercentage = (value, max) => {
  return value * 1.0 / max;
};
export const filterItems = (items, filter) => {
  let filteredItems = items;
  if (filter === 'DONE') {
    filteredItems = items.filter(item => item.checked);
  } else if (filter === 'TODO') {
    filteredItems = items.filter(item => !item.checked);
  }
  return filteredItems;
};
